Ext.define('MyApp.view.BreadCrumbs', {
    extend: 'Ext.Component',
    alias: 'widget.breadcrumbs',

    cls: 'breadcrumbs',
    itemId: 'breadcrumbs',

    initComponent: function() {
        var me = this;

        me.addEvents(
            'breadcrumbactivate'
        );

        Ext.applyIf(me, {
            tpl: [
                '<tpl for=".">',
                '{% if (xindex < xcount) { %}',
                '    <span class="prev" data-card="{[xindex - 1]}">{.}</span> &gt;',
                '{% } else { %}',
                '    <span class="current">{.}</span>',
                '{% } %}',
                '</tpl>'
            ],
            listeners: {
                afterrender: {
                    fn: me.onBreadcrumbsAfterRender,
                    scope: me
                }
            }
        });

        me.callParent(arguments);
    },

    onBreadcrumbsAfterRender: function(component, eOpts) {
        component.mon(component.getEl(), 'click', function(e) {
        	var target = e.getTarget('.prev', 1);
            if (target) {
                component.fireEvent('breadcrumbactivate', component, parseInt(target.getAttribute('data-card'), 10));
            }
        });
    }

});